body.script:
        $(document).ready(function(){



            $( "[data-copyright]" ).each(function( index ) {
                var data = $( this ).data('copyright');
                if (data != '') {
                    $(this).wrap( "
" );
                    $('.figure__image--copyright-'+index).prepend(''+data+'');
                }

                if ($(window).width() > 1110) {
                    $('.figure__image--copyright-'+index).mouseenter(function() {
                        $('#cpyr-'+index).fadeIn(300);
                    });

                    $('.figure__image--copyright-'+index).mouseleave(function() {
                        $('#cpyr-'+index).fadeOut(300);
                    });
                }
            });
             function initializeContactIframe() {
                    var iFrame = $('[data-contact-iframe-src]');
                    iFrame.each(function( index ) {
                       if($(this) != '' || typeOf($(this)) === undefined){
                            index++;

                            $.urlParam = function (name) {
                                var results = new RegExp('[\?&]' + name + '=([^&#]*)').exec(window.location.href);
                                if (results == null) {
                                    return '';
                                }
                                return decodeURI(results[1]) || 0;
                            }
                            var contactID = $.urlParam('id');
                            var parameterSign = '';
                            if ($(this).attr('data-contact-iframe-src')) {
                                parameterSign = '?id='+contactID;
                                if ($(this).attr('data-contact-iframe-src').indexOf('?') > -1) {
                                  parameterSign = '&id='+contactID;
                                }   
                            }
                            var iFrameTemplate ='';
                            var iFrameDom = $.parseHTML(iFrameTemplate);

                            $(this).append(iFrameDom);
                            window.addEventListener("message", resizeIframe, false);
                        	function resizeIframe(post){
                        	  	var iframe = $(iFrame); // ID des IFrames
                        		if (iframe.length <= 0) return;

                        		iframe.css('transition', 'height 1s ease');
                        		iframe.css('transition-delay', '0.5s');
                        		iframe.height(post.data.height);
                        		iframe.css('overflow', 'hidden');

                        		if(!post.data.formId && post.data.height) {
                        		    setTimeout(function() {
                        		        $('html, body').animate({
                                            scrollTop: 0
                                        }, 1000);
                        		    }, 0)
                        		}
                        	}

                        	var iframeIndex = 'embediframe-'+index;

                            var ifr=document.getElementById(iframeIndex);
                            ifr.onload=function(){
                                $(this).iFrameResize([{ log: false }]);
                            };
                        }
                    });
                }

                initializeContactIframe();
        });

